import numpy as np
import pandas as pd
from sklearn.preprocessing import StandardScaler
from sklearn import datasets, linear_model
from sklearn.model_selection import train_test_split
from sklearn import svm
from sklearn.metrics import accuracy_score,mean_squared_error
import csv
import matplotlib.pyplot as plt
import seaborn as sb
import joblib

"""2.  **Importation de DataSet**

Lecture du fichier CSV **importé**
"""

data = pd.read_csv("data.csv")
#print(list(data))

"""Affichage du fichier sous forme d'une liste avec tout les columns """

#Affichage des 5 Premiers élements de la liste
data.head()

"""**Dans cette partie on vérifie si on a des columns vide qui ne comporte pas de données**"""

data.isna().sum()

"""**Affichage du column Car**"""

